import React from "react";
import { useState, useEffect } from 'react';
import StudentForm from './StudentForm';
import { useFetch } from '../service/StudentService';

const StudentModal = (props) => {

    let student = null;

    const { loading, data } = useFetch('getById', props.studentID);

    if (data.id !== -1) student = data;
    console.log(student);


    return (
        <div>
            {student && <Form />}
            {/* {!student && <Form/>} */}
        </div>
    );

    function Form(props) {
        return (
            <div className="modal fade" id='studentModal'>
                <div className="modal-dialog modal-xl">
                    <div className="modal-content">

                        <div className="modal-header">
                            <h4 className="modal-title">Update {student.firstName + ' ' + student.lastName}</h4>
                            <button type="button" className="close" data-dismiss="modal">&times;</button>
                        </div>

                        <div className="modal-body">
                            <StudentForm student ={student}/>
                        </div>

                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        </div>

                    </div>
                </div>
            </div>
        );
    }

}

export default StudentModal;
