import React, { Component } from 'react';
import './form.css';
import { useState, useEffect } from 'react';
import { updateStudent } from '../service/StudentService';

const StudentForm = (props) => {
    //State for the form
    const [id, setId] =  useState(props.student.id);
    const [firstName, setFirstName] =  useState(props.student.firstName);
    const [lastName, setLastName] =  useState(props.student.lastName);
    const [email, setEmail] =  useState(props.student.email);
    const [department, setDepartment] =  useState(props.student.department);
    const [imagePath, setImagePath] =  useState(props.student.imagePath);

    // useEffect(() => {
    //     setState(props);
    //     return () => {
    //         setState({});
    //     };
    // }, [props]);

    function handleChange(event) {

    }

    function handleSubmit() {

        const putStudent = {

        }
        console.log(putStudent);
        updateStudent(putStudent);
    }

    return (
        <div>
            {id && <div className="form-style">
                <h3>{'Changes for ' + firstName + ' ' + lastName}</h3>
                <form>
                    <div className="form-group">
                        <label >First Name:</label>
                        <input type="text" name="firstName" className="form-control" id="firstName" value={firstName} onChange={e => setFirstName(e.target.value)}/>
                    </div>

                    <div className="form-group">
                        <label >Last Name:</label>
                        <input type="text" name="lastName" className="form-control" id="lastName" value={lastName} onChange={e => setLastName(e.target.value)}/>
                    </div>

                    <div className="form-group">
                        <label >email:</label>
                        <input type="text" name="email" className="form-control" id="email" value={email} onChange={e => setEmail(e.target.value)} />
                    </div>

                    <div className="form-group">
                        <label >Department:</label>
                        <input type="text" name="department" className="form-control" id="department" value={department} onChange={e => setDepartment(e.target.value)}/>
                    </div>

                    <div className="form-group">
                        <label >Image URL:</label>
                        <input type="text" name="imagePath" className="form-control" id="imagePath" value={imagePath} onChange={e => setImagePath(e.target.value)}/>
                    </div>

                    <input type="submit" value="Submit" className="btn btn-primary" onClick={handleSubmit}/>
                </form>

            </div>}
        </div>
    );
}

export default StudentForm;
