import React from "react";
import  useFetch from "../service/StudentService"; 
import Card from '../components_example/Card';
import { Component, useState, useEffect } from 'react';

function FuncCardSet(props) {
    const { loading, data } = useFetch('getAll', null);
    const {cardList, setCardList} = useState(data);
    console.log(data);
    console.log(loading);

    const handleDelete = (id) => {
        var removeCard = document.getElementById(id);
        removeCard.parentNode.removeChild(removeCard);
    }

  
    return (
      <div>
        {loading && <p>loading...</p>}
        {data &&
          data.length > 1 &&
          data.map(student => <Card key = {student.id} info = {student} handleDelete = {handleDelete}/>)}
        {/* {data && data.length > 0 && <Card info = {data.id} /> } */}
      </div>
    );
  }

  export default FuncCardSet;
