import React from 'react';
import { updateStudent } from '../service/StudentService';
import StudentModal from './StudentModal';

const ModalController = (purpose, id) => {
    console.log(purpose, id);
    let initial = true;
    let update = false;
    let create = false;
    switch (purpose) {
        case 'update':
            initial = false;
            update = true;
            create = false;
            break;
        case 'create':
            initial = false;
            update = false;
            create = true;
            break;
        default:
    }

    return (
        <div>
            {initial && <StudentModal />}
            {update &&
                <StudentModal studentID={id}/>}
            {create &&
                <StudentModal />}
        </div>
    );

} 

export default ModalController;
