import React from 'react';
import { updateStudent } from '../service/StudentService';
import StudentModal from './StudentModal';

const ModalController = (props) => {

    let purpose = props.modalState.purpose;
    let id = props.modalState.id;
    console.log(purpose, id);
    let initial = true;
    let update = false;
    let create = false;
    switch (purpose) {
        case 'update':
            initial = false;
            update = true;
            create = false;
            break;
        case 'create':
            initial = false;
            update = false;
            create = true;
            break;
        default:
            initial = true;
    }

    return (
        <div>
            {initial && <StudentModal />}
            {update &&
                <StudentModal studentID={id}/>}
            {create &&
                <StudentModal />}
        </div>
    );

} 

export default ModalController;
