import React, { Component } from 'react';
import './form.css';
import { useState, useEffect } from 'react';
import { updateStudent } from '../service/StudentService';

const StudentForm = (props) => {

    const [state, setState] = useState(props);

    useEffect(() => {
        setState(props);
        return () => {
            setState({});
        };
    }, [props]);

    function handleChange(event) {

    }

    function handleSubmit(event) {
        updateStudent(event.target);
    }

    console.log(props.student.firstName);
    return (
        <div>
            {state && <div className="form-style">
                <h3>{state.student.firstName}</h3>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label >First Name:</label>
                        <input type="text" name="color" className="form-control" id="color" onChange={handleChange} />
                    </div>

                    <div className="form-group">
                        <label >Last Name:</label>
                        <input type="text" name="color" className="form-control" id="color" onChange={handleChange} />
                    </div>

                    <div className="form-group">
                        <label >email:</label>
                        <input type="text" name="color" className="form-control" id="color" onChange={handleChange} />
                    </div>

                    <div className="form-group">
                        <label >Department:</label>
                        <input type="text" name="color" className="form-control" id="color" onChange={handleChange} />
                    </div>

                    <div className="form-group">
                        <label >Image URL:</label>
                        <input type="text" name="color" className="form-control" id="color" onChange={handleChange} />
                    </div>

                    <input type="submit" value="Submit" className="btn btn-primary" />
                </form>

            </div>}
        </div>
    );
}

export default StudentForm;
