import React from "react";
import { useState, useEffect } from 'react';
import StudentForm from './StudentForm';
import { useFetch } from '../service/StudentService';
import {updateStudent} from '../service/StudentService';

const StudentModal = (props) => {

    const [id, setId] =  useState(props.student.id);
    const [firstName, setFirstName] =  useState(props.student.firstName);
    const [lastName, setLastName] =  useState(props.student.lastName);
    const [email, setEmail] =  useState(props.student.email);
    const [department, setDepartment] =  useState(props.student.department);
    const [imagePath, setImagePath] =  useState(props.student.imagePath);

    // Get the student from props to pass to the form
    let student = props.student;

    function handleSubmit(data) {
        console.log('In Submit function');
        console.log(data);
        updateStudent(data);
    }

    return (
        <div>
            {student && <Form student = {student}/>}
        </div>
    );

    function Form(props) {

        return (
            <div className="modal fade" id='studentModal'>
                <div className="modal-dialog modal-xl">
                    <div className="modal-content">

                        <div className="modal-header">
                            <h4 className="modal-title">Update {student && student.firstName + ' ' + student.lastName}</h4>
                            <button type="button" className="close" data-dismiss="modal">&times;</button>
                        </div>

                        <div className="modal-body">
                            <StudentForm student ={student} handleSubmit={handleSubmit} />
                        </div>

                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

export default StudentModal;
