    function handleSubmit(event) {

        const putStudent = {
            "id": id,
            "firstName": document.getElementById("updateStudentfirstName"+id).value,
            "lastName": document.getElementById("updateStudentLastName"+id).value,
            "email": document.getElementById("updateStudentemail"+id).value,
            "imagePath": document.getElementById("updateStudentimagePath"+id).value,
            "department": document.getElementById("updateStudentdepartment"+id).value
        }
        updateStudent(event.target);
    }

    return (
        <div>
            {state && <div className="form-style">
                <h3>{'Changes for ' + state.student.firstName + ' ' + state.student.lastName}</h3>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label >First Name:</label>
                        <input type="text" name="firstName" className="form-control" id="firstName" onChange={handleChange} value={state.student.firstName}/>
                    </div>

                    <div className="form-group">
                        <label >Last Name:</label>
                        <input type="text" name="lastName" className="form-control" id="lastName" onChange={handleChange} value={state.student.lastName} />
                    </div>

                    <div className="form-group">
                        <label >email:</label>
                        <input type="text" name="email" className="form-control" id="email" onChange={handleChange} value={state.student.email}/>
                    </div>

                    <div className="form-group">
                        <label >Department:</label>
                        <input type="text" name="department" className="form-control" id="department" onChange={handleChange} value={state.student.department}/>
                    </div>

                    <div className="form-group">
                        <label >Image URL:</label>
                        <input type="text" name="imagePath" className="form-control" id="imagePath" onChange={handleChange} value={state.student.imagePath}/>
                    </div>

                    <input type="submit" value="Submit" className="btn btn-primary" />
                </form>

            </div>}
        </div>
    );
}

export default StudentForm;
