import React from "react";
import { useState, useEffect } from 'react';
import StudentForm from './StudentForm';
import { useFetch } from '../service/StudentService';
import { updateStudent } from '../service/StudentService';

const StudentModal = (props) => {

    // Get the student from props to pass to the form
    let student = props.student;

    const handleChange = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});
    }

    const onSubmit = (event) => {
        event.preventDefault();
        console.log('weird');
    }

    const handleSubmit = (data) => {
        console.log(data);
    }


    return (
        <div>
            {student && <Form student={student} />}
        </div>
    );

    function Form(props) {

        return (
            <div className="modal fade" id='studentModal'>
                <div className="modal-dialog modal-xl">
                    <div className="modal-content">

                        <div className="modal-header">
                            <h4 className="modal-title">Update {student && student.firstName + ' ' + student.lastName}</h4>
                            <button type="button" className="close" data-dismiss="modal">&times;</button>
                        </div>

                        <div className="modal-body">
                            <StudentForm student={student} />
                        </div>

                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

export default StudentModal;
