import React from 'react';
import StudentModal from './StudentModal';
import { useFetch } from '../service/StudentService';
import { useState, useEffect } from 'react';

const ModalController = (props) => {

    const { loading, data } = useFetch('getById', id);
    let purpose = props.modalState.purpose;
    let id = props.modalState.id;

    const [state, setState] = useState({purpose, id});

    let student = data;

    let initial = true;
    let update = false;
    let create = false;
    switch (purpose) {
        case 'update':
            initial = false;
            update = true;
            create = false;
            break;
        case 'create':
            initial = false;
            update = false;
            create = true;
            break;
        default:
            initial = true;
    }

    return (
        <div>
            {<StudentModal student={student} />}
            {/* {update &&
                <StudentModal studentID={id}/>}
            {create &&
                <StudentModal />} */}
        </div>
    );
}
export default ModalController;
