import React from "react";
import Card from './Card';
import { useFetch } from '../service/StudentService';

function CardLayout(props) {
  const { loading, data } = useFetch('getAll', null);

  let cardCount = 0;
  let deckMax = 4;
  let maxSet = false;
  cardCount % deckMax === 0 ? maxSet = true : maxSet = false;

  const CardDeck = (props) => {
    console.log(props.students);
    return(<>
      <div className='card-deck'>
        {/* {props.student.map((student, cardCount) => <Card key={student.id} info={student} handleDelete={props.handleDelete} handleUpdate={props.handleUpdate} />)} */}
      </div>
    </>);
  }

  return (
    <div>
      {loading && <p>loading...</p>}
      {data &&
        data.length > 1 &&
        data.map(students => <CardDeck students = {students} />)}
      {/* {data && data.length > 0 && <Card info = {data.id} /> } */}
    </div>
  );
}

export default CardLayout;
