import React from 'react';
import StudentModal from './StudentModal';
import { useFetch } from '../service/StudentService';
import { useState, useEffect } from 'react';

const ModalController = (props) => {

    // let purpose = props.modalState.purpose;
    // let id = props.modalState.id;
    // console.log(purpose, id);
    console.log(props.modalUpdate);
    console.log(props.modalState.purpose);
    let {purpose, id} = props.modalUpdate(props.modalState.purpose, props.modalState.id);
    console.log(purpose, id);

    let student = null;

    const { loading, data } = useFetch('getById', id);

    student = data;
    console.log(student);

    // const [state, setState] = useState({});

    // useEffect(() => {
    //     setState({purpose, id});
    // }, [id]);

    let initial = true;
    let update = false;
    let create = false;
    switch (purpose) {
        case 'update':
            initial = false;
            update = true;
            create = false;
            break;
        case 'create':
            initial = false;
            update = false;
            create = true;
            break;
        default:
            initial = true;
    }

    return (
        <div>
            {<StudentModal student={student} />}
            {/* {update &&
                <StudentModal studentID={id}/>}
            {create &&
                <StudentModal />} */}
        </div>
    );
}
export default ModalController;
