        {create &&
          <StudentModal/>}
      </div>
    );

  }

  return (
    <div className="container">

      <FuncCardSet handleModal={handleModal} />
      {/* <StudentModal handleModal={handleModal} /> */}

    </div>
  );
}

export default App;
