import React from "react";
// import  useFetch from "../service/StudentService"; 
import Card from '../components_example/Card';
import { deleteStudent, useFetch } from '../service/StudentService';
import StudentModal from "./StudentModal";
import { useState, useEffect } from 'react';

function FuncCardSet(props) {
  const { loading, data } = useFetch('getAll', null);
  const [modal, setModal] = useState(props.handleModal);
  const [students, setStudents] = useState(data);

  const handleDelete = (id) => {
    let ok = window.confirm('Are you sure you wish to delete this student?\nPress "Ok" to confirm');
    if (ok === true) {
      var removeCard = document.getElementById('card' + id);
      removeCard.parentNode.removeChild(removeCard);
      deleteStudent(id);
      window.alert('Student has been deleted');
    }
  }

  const handleUpdate = (id) => {
    props.handleModal('update', id);
    return id;
  }

  return (
    <div>
      {loading && <p>loading...</p>}
      {data &&
        data.length > 1 &&
        data.map(student => <Card key={student.id} info={student} handleDelete={handleDelete} handleUpdate={handleUpdate} />)}
      {/* {data && data.length > 0 && <Card info = {data.id} /> } */}
    </div>
  );
}

export default FuncCardSet;
