    const [state, setState] = useState(props);

    //State for the form
    const [id, setId] =  useState(state.student.id);
    const [firstName, setFirstName] =  useState(state.student.firstName);
    const [lastName, setLastName] =  useState(state.student.lastName);
    const [email, setEmail] =  useState(state.student.email);
    const [department, setDepartment] =  useState(state.student.department);
    const [imagePath, setImagePath] =  useState(state.student.imagePath);

    useEffect(() => {
        setState(props);
        return () => {
            setState({});
        };
    }, [props]);

