        <div>
            {state && <div className="form-style">
                <h3>{'Changes for ' + state.student.firstName + ' ' + state.student.lastName}</h3>
                <form>
                    <div className="form-group">
                        <label >First Name:</label>
                        <input type="text" name="firstName" className="form-control" id="firstName" value={firstName}/>
                    </div>

                    <div className="form-group">
                        <label >Last Name:</label>
                        <input type="text" name="lastName" className="form-control" id="lastName" value={state.student.lastName} />
                    </div>

                    <div className="form-group">
                        <label >email:</label>
                        <input type="text" name="email" className="form-control" id="email" value={state.student.email}/>
                    </div>

                    <div className="form-group">
                        <label >Department:</label>
                        <input type="text" name="department" className="form-control" id="department" value={state.student.department}/>
                    </div>

                    <div className="form-group">
                        <label >Image URL:</label>
                        <input type="text" name="imagePath" className="form-control" id="imagePath" value={state.student.imagePath}/>
                    </div>

                    <input type="submit" value="Submit" className="btn btn-primary" onClick={handleSubmit}/>
                </form>

            </div>}
        </div>
    );
}

export default StudentForm;
