import React from 'react';
import './App.css';
import FuncCardSet from './components_example/FuncCardSet';
import {useState, useEffect} from 'react';
import ModalController from './components_example/ModalController';

function App() {



  function modalRelay(purpose, id) {

    return {purpose, id};
  }

  const [modalState, setModalState] = useState({});

  useEffect(() => {
    setModalState(modalRelay);
    return () => {
      setModalState({});
    };
  }, [modalState]);

  return (
    <div className="container">

      <FuncCardSet handleModal={modalRelay} />
      <ModalController handleModal={modalRelay} />

    </div>
  );
}

export default App;
