import React, {Component} from 'react';
import './Card.css';

class Card extends Component {

    render() {

        return (
            <div className="card">
                <Header firstName={this.props.info.firstName} lastName = {this.props.info.lasName} />
                <Body url={this.props.info.imagePath} email = {this.props.info.email} dept = {this.props.info.department}/>
                <Footer details={this.props.details} />
                <Footer/>
            </div>

        );
    }
}

function Header(props) {
    return (
        <div className="card-header">
            {props.title}
        </div>
    );
}

function Body(props) {
    console.log(props);
    return(
        <div className="card-body">
            <img src={props.url} alt={props.imgAlt} />
            <div className="container">
                <p>Name: {props.name}</p>
                <p>Series: {props.series}</p>
            </div>
        </div>
    );
}

function Footer(props) {
    return(
        <div className="card-footer">
            {props.details}
        </div>
    );
}

// exposes the Card to other models/files so it can imported and used there
export default Card;
