import React from 'react';
import './App.css';
import FuncCardSet from './components_example/FuncCardSet';
import {useState, useEffect} from 'react';
import ModalController from './components_example/ModalController';

function App() {

  const [modalState, setModalState] = useState({});

  function modalRelay(purpose, id) {
   setModalState({purpose, id});
    return {purpose, id};
  }

  useEffect(() => {
    setModalState(modalRelay);
    return () => {
      setModalState({});
    };
  }, [modalState]);

  return (
    <div className="container">
      <FuncCardSet modalRelay={modalRelay} />
      <ModalController modalState={modalState} />

    </div>
  );
}

export default App;
