import React, { useState } from 'react';
import { Button, Modal } from 'react-bootstrap';
import StudentForm from './StudentForm';

const  FormModal = (props) => {
    const [show, setShow] = useState(false);
    const[type, setType] = useState(props.type);
  
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const ButtonContext = (props) => {
      let text = props.type.toString().toUpperCase();
      let color = '';
      switch(props.type){
        case 'create': color = 'primary';
        break;
        case 'update': color = 'warning';
        break;
        default:
      }

      return(
        <Button variant={color} onClick={handleShow}>
          {text}
        </Button>
      );
    }   
  
    return (
      <>
        <ButtonContext type={type}/>
  
        <Modal show={show} onHide={handleClose} size='lg'>
          <Modal.Header closeButton>
            <Modal.Title>Update Student</Modal.Title>
          </Modal.Header>
          <Modal.Body><StudentForm student={props.student} handleUpdate={props.handleUpdate}/></Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }

  export default FormModal;
