import React, { Component } from 'react';

class AddEvent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            sum: 0
        }

        //this.add = this.add.bind(this);
    }

    add(num) {
        this.setState(
            state => ({
                sum: state.sum + num
            })
        );
    }

    render() {

        const myStyle = {
            color: this.props.textColor,
            backgroundColor: 'pink'
        };

        return(
            <div>
                <p style={myStyle}>Sum: {this.state.sum}</p>
                <button onClick={ this.add.bind(this, 1) } > Add One </button>
                <button onClick={ (e) => this.add(2, e) } >Add Two</button>
                <button onClick={ (e) => this.add(3, e) } >Add Three</button>
            </div>
        );
    }

}

export default AddEvent;
