import React from "react";
import  useFetch from "../service/StudentService"; 

function FuncCard(props) {
    const { loading, data } = useFetch('getAll','null');
    console.log(data);
    console.log(loading);

    function Header(props) {
        return (
            <div className="card-header">
                {props.title}
            </div>
        );
    }

    function Body(props) {
        return(
            <div className="card-body">
                <img src={props.url} alt={props.imgAlt} />
                <div className="container">
                    <p>Name: {props.name}</p>
                    <p>Series: {props.series}</p>
                </div>
            </div>
        );
    }

    function Footer(props) {
        return(
            <div className="card-footer">
                {props.details}
            </div>
        );
    }


  
    return (
      <div>
        {loading && <p>loading...</p>}
        {data &&
          data.length > 0 &&
          data.map(student => <p key={student.id}>{student.firstName}</p>)}
      </div>
    );
  }

  export default FuncCard;


  class Card extends Component {

    render() {
        return (
            <div className="card">
                <Header title="Character Card" />
                <Body url={this.props.url} imgAlt={this.props.imgAlt} name={this.props.name} series={this.props.series}/>
                <Footer details={this.props.details} />
                <Footer/>
            </div>

        );
    }
}




