import React, { Component } from 'react';
import './form.css';
import { useState, useEffect } from 'react';

const StudentForm = (props) => {

    const [state, setstate] = useState({});


    return (
        <div className="form-style">

            <h3>Survey</h3>
            {/* onSubmit={this.handleSubmit} */}
            <form >
                <div className="form-group">
                    <label for="color">Favorite Color</label>
                    <input type="text" name="color" className="form-control" id="color" />
                    <p>Value: </p>
                </div>

                <div className="form-group">
                    <label for="tea">Best Arizon Ice Tea Flavor</label>
                    <select value={this.state.tea} onChange={this.handleChange} name="tea" className="form-control" id="tea">
                        <option value="disabled" disabled={true}>Select Tea</option>
                        <option value="green" >Green Tea</option>
                        <option value="mango" >Mango Tea</option>
                        <option value="peach" >Peach Tea</option>
                        <option value="lemon" >Lemon Tea</option>
                        <option value="half" >Half-and-Half Tea</option>
                    </select>
                    <p>Tea: {this.state.tea}</p>
                </div>

                <input type="submit" value="Submit" className="btn btn-primary" />
            </form>

        </div>
    );
}

export default StudentForm;
