import React, { useState, useContext } from 'react';
import { Button, Modal } from 'react-bootstrap';
import StudentForm from './StudentForm';
import {createStudent} from '../service/StudentService';

const FormModal = (props) => {
  const [show, setShow] = useState(false);
  const [type, setType] = useState(props.type);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  
  var buttonColor, buttonText, crudFunction;

  switch(type){
    case 'create':
      buttonColor = 'primary';
      buttonText = type.toString().toUpperCase();
      crudFunction = (student) => {createStudent(student);}
      break;
      case 'update':
        break;
        default:
  }

  const themes = {
    create: {
      buttonColor: 'primary',
      buttonText: type.toString().toUpperCase(),
      crudFunction: (student) => {createStudent(student);}
    },
    update: {
      buttonColor: 'primary',
      buttonText: type.toString().toUpperCase(),
      crudFunction: props.handleUpdate
    }
  };

  return (
    <>
      {<Button variant={color} onClick={handleShow}>
        {text}
      </Button>}

      <Modal show={show} onHide={handleClose} size='lg'>
        <Modal.Header closeButton>
          <Modal.Title>Update Student</Modal.Title>
        </Modal.Header>
        <Modal.Body><StudentForm student={props.student} handleUpdate={props.handleUpdate} /></Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
            </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default FormModal;
