import React, { Component } from 'react';
import './form.css';
import { useState, useEffect } from 'react';
import {updateStudent} from '../service/StudentService';

const StudentForm = (props) => {

    const [student, setState] = useState({
        'id': null,
        'firstName': null,
        'lastName': null,
        'email': null
    });

    function handleChange(event) {
        setstate(event.target.value);
    }

    function handleSubmit(event) {
        updateStudent(event.target);
    }

    return (
        <div>
            {props && <div className="form-style">
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label >Favorite Color</label>
                        <input type="text" name="color" className="form-control" id="color" onChange={handleChange} />
                        <p>Value: </p>
                    </div>

                    <input type="submit" value="Submit" className="btn btn-primary" />
                </form>

            </div>}
        </div>
    );
}

export default StudentForm;
