import React from 'react';
import StudentModal from './StudentModal';
import { useFetch } from '../service/StudentService';
import { useState, useEffect } from 'react';

const ModalController = (props) => {

    let purpose = props.modalState.purpose;
    let student = props.modalState.student;

    const [state, setState] = useState(student);

    useEffect(() => {
        setState(student);
        console.log(state);
        return () => {};
    }, [props]);

    let initial = true;
    let update = false;
    let create = false;
    switch (purpose) {
        case 'update':
            initial = false;
            update = true;
            create = false;
            break;
        case 'create':
            initial = false;
            update = false;
            create = true;
            break;
        default:
            initial = true;
    }

    return (
        <div>
            {/* {initial && <StudentModal/>} */}
            <StudentModal student={state} />
            {/* {create &&
                <StudentModal />} */}
        </div>
    );
}
export default ModalController;
