    return (
        <div className="form-style">

            <h3>Survey</h3>
            {/* onSubmit={this.handleSubmit} */}
            <form >
                <div className="form-group">
                    <label >Favorite Color</label>
                    <input type="text" name="color" className="form-control" id="color" />
                    <p>Value: </p>
