import React, { Component } from 'react';
import './form.css';
import { useState, useEffect } from 'react';
import { updateStudent } from '../service/StudentService';

const StudentForm = (props) => {

    const [student, setState] = useState(props);

    function handleChange(event) {

    }

    function handleSubmit(event) {
        updateStudent(event.target);
    }

    console.log(props.student.firstName);
    return (
        <div>
            {student && <div className="form-style">
                <h3>{student.firstName}</h3>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label >First Name:</label>
                        <input type="text" name="color" className="form-control" id="color" onChange={handleChange} />
                        <p>Value: </p>
                    </div>

                    <input type="submit" value="Submit" className="btn btn-primary" />
                </form>

            </div>}
        </div>
    );
}

export default StudentForm;
